// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.6.0 < 0.8.0;

/** 
 * @title StatementVoting
 * @author IP Sheung Chiu, Li Chun Ngai
 * Implements voting process along with vote delegation and added funciton that allow users to get more information
 */
contract Voting {

    struct EventInformation {
        uint totalproposal; // total proposal that in the voting event
        uint totalvote; // total vote that the chairperson give out
    }

    struct Voter {
        uint weight; // weight is accumulated by delegation
        bool voted;  // if true, that person already voted
        address delegate; // person delegated to
        uint vote;   // index of the voted proposal
    }

    struct Proposal {
        // If you can limit the length to a certain number of bytes, 
        // always use one of bytes1 to bytes32 because they are much cheaper
        uint name;   // short name (up to 32 bytes)
        uint voteCount; // number of accumulated votes
    }

    struct Candidate {
        address Address; //To store the candidate address who can vote in this voting event
    }

    address public chairperson; //The address of the chairperson who deploy the smart contract

    mapping(address => Voter) public voters;

    Proposal[] public proposals;
    EventInformation public votingevent;
    Candidate[999999999999] public candidate;
	uint public winningProposalPublic;

    /** 
     * @dev Create a new ballot to choose one of 'proposalNames'.
     * @param proposalNames names of proposals
     */
    constructor(uint[] memory proposalNames){
        chairperson = msg.sender;
        voters[chairperson].weight = 1;
        votingevent.totalproposal = proposalNames.length;
		votingevent.totalvote = 1;
		winningProposalPublic = 0;

        for (uint i = 0; i < proposalNames.length; i++) {
            // 'Proposal({...})' creates a temporary
            // Proposal object and 'proposals.push(...)'
            // appends it to the end of 'proposals'.
            proposals.push(Proposal({
                name: proposalNames[i],
                voteCount: 0
            }));
        }
    }

    /** 
     * @dev Give 'voter' the right to vote on this ballot. May only be called by 'chairperson'.
     * @param voter address of voter
     */
    function giveRightToVote(address voter) public {
        require(
            msg.sender == chairperson,
            "Only chairperson can give right to vote."
        );
        require(
            !voters[voter].voted,
            "The voter already voted."
        );
        require(voters[voter].weight == 0);
        voters[voter].weight = 1;
        candidate[votingevent.totalvote].Address = voter;
        votingevent.totalvote += 1;
    }

    /**
     * @dev Delegate your vote to the voter 'to'.
     * @param to address to which vote is delegated
     */
    function delegate(address to) public {
        Voter storage sender = voters[msg.sender];
        require(!sender.voted, "You already voted.");
        require(to != msg.sender, "Self-delegation is disallowed.");

        while (voters[to].delegate != address(0)) {
            to = voters[to].delegate;

            // We found a loop in the delegation, not allowed.
            require(to != msg.sender, "Found loop in delegation.");
        }
        sender.voted = true;
        sender.delegate = to;
        Voter storage delegate_ = voters[to];
        if (delegate_.voted) {
            // If the delegate already voted,
            // directly add to the number of votes.
            proposals[delegate_.vote].voteCount += sender.weight;
        } else {
            // If the delegate did not vote yet,
            // add to her weight.
            delegate_.weight += sender.weight;
        }
	//winningProposal();
    }

    /**
     * @dev Give your vote (including votes delegated to you) to proposal 'proposals[proposal].name'.
     * @param proposal index of proposal in the proposals array
     */
    function vote(uint proposal) public {
        Voter storage sender = voters[msg.sender];
        require(sender.weight != 0, "Has no right to vote");
        require(!sender.voted, "Already voted.");
        sender.voted = true;
        sender.vote = proposal;

        // If 'proposal' is out of the range of the array,
        // this will throw automatically and revert all changes.
        proposals[proposal].voteCount += sender.weight;
        winningProposal();
    }


    /** 
     * @dev Computes the winning proposal taking all previous votes into account.
     * @return winningProposal_ index of winning proposal in the proposals array
     */
	//bool public tieCheck = false;
    function winningProposal() public
            returns (uint winningProposal_)
    {
        uint winningVoteCount = 0;
        //bool tieCheck = false;
        for (uint p = 0; p < proposals.length; p++) {
            if (proposals[p].voteCount > winningVoteCount) {
                winningVoteCount = proposals[p].voteCount;
                winningProposal_ = p;
                //tieCheck = false;
            } else if (proposals[p].voteCount == winningVoteCount) {
                //tieCheck = true;
            }
        }
        winningProposalPublic = winningProposal_;
        // If 'tieCheck' is true,
        // which mean there are more than one winner,
        // it will not return anythings.
        // you can use viewRank() to check the result.
        //require(tieCheck == false, "The event got tiebreak.");
    }

    /** 
     * @dev Calls winningProposal() function to get the index of the winner contained in the proposals array and then
     * @return winnerName_ the name of the winner
     * @return voteCount_ the vote count of the winner
     */
     /*
    function winnerName() public view
            returns (uint winnerName_, uint voteCount_)
    {
        winnerName_ = proposals[winningProposal()].name;
        voteCount_ =  proposals[winningProposal()].voteCount;
    }
    */
    /**
     * @dev Call viewVoter(uint proposal) funciton to get the voter address who voted to that proposal
     * @param proposal the proposal that you wnat to find out the voter adress
     * @return votersAddress an array of voter address
     */
    function viewVoter(uint proposal) public view
                returns (address[] memory votersAddress)
    {
        uint mazVoteCount = proposals[proposal].voteCount;
        address[] memory voterAddressArray = new address[](mazVoteCount);
        uint p = 0;
        for (uint i = 0; i < votingevent.totalvote; i++){
            if (voters[candidate[i].Address].vote == proposal){
                voterAddressArray[p] = candidate[i].Address;
                p += 1;
            }
        }
        return voterAddressArray;
    }

    /**
     * @dev Call viewRank() function to get the rank of the proposal
     * @return rankName ranked proposal name 
     * @return rankVote ranked proposal vote count
     */
    function viewRank() public view
                returns (uint[] memory rankName, uint[] memory rankVote)
    {
        Proposal[] memory tempProposal = new Proposal[](votingevent.totalproposal);
        for (uint i = 0; i < votingevent.totalproposal; i++){
            tempProposal = proposals;
        }
        Proposal memory swapProposal;
        for (uint k = 0; k < votingevent.totalproposal; k++){
            for (uint j = 0; j < votingevent.totalproposal - 1 - k; j++){
                if (tempProposal[j].voteCount < tempProposal[j + 1].voteCount){
                    swapProposal = tempProposal[j];
                    tempProposal[j] = tempProposal[j + 1];
                    tempProposal[j + 1] = swapProposal;
                }
            }
        }
        uint[] memory tempname = new uint[](votingevent.totalproposal);
        for (uint p = 0; p < votingevent.totalproposal; p++){
            tempname[p] = tempProposal[p].name;
        }
        uint[] memory tempvote = new uint[](votingevent.totalproposal);
        for (uint q = 0; q < votingevent.totalproposal; q++){
            tempvote[q] = tempProposal[q].voteCount;
        }
        return (tempname, tempvote);
    }
	Voter public showVoter = voters[chairperson];
	function voterInfo(address voter) public {
		showVoter = voters[voter];
	}
}

